import { useState } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import rtn from "../assets/return.png";
import coin from "../assets/Coin-W.svg";
export default function CheckoutPage() {
  const [counter, setCounter] = useState(0);
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [card, setCard] = useState("");

  console.log(card, address, email);

  const navigate = useNavigate();
  function returnPage() {
    navigate("/Home");
  }

  function addCounter(){
    setCounter(counter+1)
  }
  function subtractCounter(){
    setCounter(counter-1)
  }

  return (
    <StyledCheckout>
      <Main>
        <Return src={rtn} onClick={returnPage} />
        <UserInfos>
          <h1>
            You own *x* <img src={coin} alt="Coin" />
          </h1>

          <input
            type="email"
            placeholder="Type in your email"
            onChange={(e) => setEmail(e.target.value)}
          />
          <ArtCoins>
            {" "}
            <h1> ArteCoins</h1>{" "}
            <Counter>
              {" "}
              <button>+</button> <h3>{counter}</h3> <button>-</button>
            </Counter>{" "}
          </ArtCoins>

          <input
            type="text"
            placeholder="Type in your address"
            onChange={(e) => setAddress(e.target.value)}
          />
          <input
            type="number"
            placeholder="Type in your credit card"
            onChange={(e) => setCard(e.target.value)}
          />
        </UserInfos>
        <Checkout>
          <div>
            <h1> Checkout</h1>
          </div>
          <div>
            <h3> {counter} </h3> <img src={coin} /> <h3> {} R$ </h3>{" "}
          </div>
        </Checkout>
      </Main>
    </StyledCheckout>
  );
}

const StyledCheckout = styled.div`
  background-color: black;
  height: 100vh;
  justify-content: center;
  align-items: center;
`;
const Main = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
`;

const UserInfos = styled.div`
  margin-right: 15px;
  width: 557px;
  height: 439px;
  background: #2d2d2d;
  border-radius: 54px;
  display: flex;
  flex-direction: column;
  align-items: center;
  div {
    width: auto;
    height: 10px;
    color: white;
    font-family: "Capriola";
    font-style: normal;
    font-weight: 400;
    font-size: 10px;
    line-height: 12px;
    margin-bottom: 40px;
  }
  h1 {
    color: white;
    font-size: 58px;
    margin-top: 10px;
    margin-bottom: 40px;
    img {
      width: 50px;
    }
  }
  input {
    width: 236px;
    height: 36px;
    background: #4a4a4a;
    border-radius: 7px;
    margin-bottom: 10px;
  }
`;
const ArtCoins = styled.div`
  min-width: 236px;
  min-height: 36px;
  background-color: red;
  border-radius: 7px;
  margin-bottom: 10px;
  h1 {
    font-size: 10px;
  }
`;

const Counter = styled.div`
  margin-top: 10px;
  margin-right: 20px;
  border-radius: 5px;
  width: 60px;
  height: 35px;
  align-items: center;
  display: flex;
  button {
    color: black;
    border-radius: 5px;
    width: 20px;
    height: 20px;
    border: 2px;
    border-color: black;
    cursor: pointer;
  }
`;
const Checkout = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 325px;
  height: 439px;
  background: #2d2d2d;
  border-radius: 54px;
  div {
    width: 236px;
    height: 36px;
    background: #4a4a4a;
    border-radius: 7px;
    margin-top: 10px;
    margin-bottom: 15px;
    display: flex;
    align-items: center;
    img {
      margin-right: 100px;
      width: 15px;
    }
  }
`;

const Return = styled.img`
  width: 45px;
  height: 45px;
  position: relative;
  bottom: 290px;
  cursor: pointer;
`;
